		JButton salvEdic = new JButton("SALVAR EDICAO");
		salvEdic.addActionListener(new ActionListener() {
			public void actionPerformed(ActionEvent e) {
				
				if (decifExpl.getText() != null) {
					try {
						FileWriter arq = new FileWriter(decifExpl.getText());
						PrintWriter salvarArq = new PrintWriter(arq);
						
						String chaveInput = JOptionPane.showInputDialog("INFORME A SENHA DE CRIPTOGRAFIA");
						int chave = chaveInput.length();
						String mensagem = caixaMensa.getText();
						caixaMensa.setText(Cifrador.criptCesar(mensagem, chave));
						JOptionPane.showMessageDialog(null, "Arquivo Salvo");
						salvarArq.println(caixaMensa.getText());
						salvarArq.close();
					} catch (IOException e1) {
						e1.printStackTrace();
					}
					
				}
			}
		});
		salvEdic.setFont(new Font("Tekton Pro", Font.ITALIC, 13));
		salvEdic.setBounds(173, 323, 136, 23);
